import React from 'react';

const CircularProgressBar = (props) => {

    const myTime = props.myTime;
    const circumference = 2 * Math.PI * 50; // Assuming the radius of the circle is 50

    const calculateOffset = (percentage) => {
        const progressOffset = ((100 - percentage) / 100) * circumference;
        return progressOffset.toFixed(2);
    };

    const renderProgress = () => {
        const progressOffset = calculateOffset(100);
        let offset = 0;

        return myTime.map((item, index) => {
        const progressColor = item.Color;
        const progressPercentage = item.Percentage;
        const dashArray = `${circumference} ${circumference}`;
        const dashOffset = calculateOffset(progressPercentage) - offset;

        offset += calculateOffset(progressPercentage);

        return (
            <circle
            key={index}
            className="progress-ring__circle"
            cx="50"
            cy="50"
            r="50"
            fill="transparent"
            stroke={progressColor}
            strokeWidth="10"
            strokeDasharray={dashArray}
            strokeDashoffset={dashOffset}
            />
        );
    });
  };

  return (
    <svg className="progress-ring" viewBox="0 0 100 100">
      {renderProgress()}
    </svg>
  );
};

export default CircularProgressBar;
